import { useState } from "react";
import { FaCommentDots, FaUserFriends } from "react-icons/fa";

const ConnectionRequests = () => {
  const [requests, setRequests] = useState([
    {
      id: 1,
      name: "John Smith",
      role: "Full-stack Developer",
      avatar: "data:image/jpeg;base64,",
    },
    {
      id: 2,
      name: "Sarah Johnson",
      role: "UX Designer",
      avatar: "data:image/jpeg;base64,",
    },
  ]);

  const [connectedUsers, setConnectedUsers] = useState([
    {
      id: 3,
      name: "Michael Brown",
      role: "AI Engineer • Online",
      avatar: "data:image/jpeg;base64,",
    },
    {
      id: 4,
      name: "Emily Davis",
      role: "Product Manager • Away",
      avatar: "data:image/jpeg;base64,",
    },
  ]);

  const handleAccept = (id) => {
    const acceptedUser = requests.find((user) => user.id === id);
    setConnectedUsers([...connectedUsers, acceptedUser]);
    setRequests(requests.filter((user) => user.id !== id));
  };

  const handleDecline = (id) => {
    setRequests(requests.filter((user) => user.id !== id));
  };

  return (
    <div style={{ backgroundColor: "#FFFFFF", minHeight: "100vh", width: "100vw", padding: "20px" }}>
      {/* Header */}
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-lg font-semibold">Connection Requests</h2>
        <button className="bg-black text-white px-3 py-1 rounded-lg flex items-center gap-2">
          <FaUserFriends /> Contacts
        </button>
      </div>

      {/* Incoming Requests */}
      {requests.length > 0 && (
        <div className="bg-gray-100 p-4 rounded-lg mb-4">
          <h3 className="text-md font-semibold mb-2">Incoming Requests</h3>
          {requests.map((user) => (
            <div key={user.id} className="flex items-center justify-between bg-white p-3 rounded-lg shadow mb-2">
              <div className="flex items-center gap-3">
                <img src={user.avatar} alt={user.name} className="w-10 h-10 rounded-full" />
                <div>
                  <p className="font-semibold">{user.name}</p>
                  <p className="text-sm text-gray-500">{user.role}</p>
                </div>
              </div>
              <div className="flex gap-2">
                <button onClick={() => handleAccept(user.id)} className="bg-blue-600 text-white px-3 py-1 rounded-lg">
                  Accept
                </button>
                <button onClick={() => handleDecline(user.id)} className="bg-gray-300 px-3 py-1 rounded-lg">
                  Decline
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Connected Users */}
      {connectedUsers.length > 0 && (
        <div className="bg-gray-100 p-4 rounded-lg">
          <h3 className="text-md font-semibold mb-2">Connected Users</h3>
          {connectedUsers.map((user) => (
            <div key={user.id} className="flex items-center justify-between bg-white p-3 rounded-lg shadow mb-2">
              <div className="flex items-center gap-3">
                <img src={user.avatar} alt={user.name} className="w-10 h-10 rounded-full" />
                <div>
                  <p className="font-semibold">{user.name}</p>
                  <p className="text-sm text-gray-500">{user.role}</p>
                </div>
              </div>
              <FaCommentDots className="text-black text-lg cursor-pointer" />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ConnectionRequests;