import { useState } from "react";
import { FaBell, FaCommentDots } from "react-icons/fa";

const Notifications = () => {
  const [requests, setRequests] = useState([
    {
      id: 1,
      name: "John Smith",
      role: "Full-stack Developer",
      avatar: "data:image/jpeg;base64,",
    },
  ]);

  const [accepted, setAccepted] = useState([
    {
      id: 2,
      name: "Michael Brown",
      role: "AI Engineer â€¢ Online",
      avatar: "data:image/jpeg;base64,",
    },
  ]);

  const [hackathons, setHackathons] = useState([
    {
      id: 3,
      title: "AI Innovation Challenge",
      startIn: "Starting in 2 days",
    },
  ]);

  const handleAccept = (id) => {
    const acceptedRequest = requests.find((req) => req.id === id);
    setAccepted([...accepted, acceptedRequest]);
    setRequests(requests.filter((req) => req.id !== id));
  };

  const handleDecline = (id) => {
    setRequests(requests.filter((req) => req.id !== id));
  };

  const handleClearAll = () => {
    setRequests([]);
    setAccepted([]);
    setHackathons([]);
  };

  return (
    <div style={{ backgroundColor: "#FFFFFF", minHeight: "100vh", width: "100vw", padding: "20px" }}>
      {/* Header */}
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-lg font-semibold">Notifications</h2>
        <button onClick={handleClearAll} className="bg-black text-white px-3 py-1 rounded-lg flex items-center gap-2">
          <FaBell /> Clear All
        </button>
      </div>

      {/* New Connection Requests */}
      {requests.length > 0 && (
        <div className="bg-gray-100 p-4 rounded-lg mb-4">
          <h3 className="text-md font-semibold mb-2">New Connection Requests</h3>
          {requests.map((request) => (
            <div key={request.id} className="flex items-center justify-between bg-white p-3 rounded-lg shadow mb-2">
              <div className="flex items-center gap-3">
                <img src={request.avatar} alt={request.name} className="w-10 h-10 rounded-full" />
                <div>
                  <p className="font-semibold">{request.name}</p>
                  <p className="text-sm text-gray-500">{request.role}</p>
                </div>
              </div>
              <div className="flex gap-2">
                <button onClick={() => handleAccept(request.id)} className="bg-blue-600 text-white px-3 py-1 rounded-lg">
                  Accept
                </button>
                <button onClick={() => handleDecline(request.id)} className="bg-gray-300 px-3 py-1 rounded-lg">
                  Decline
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Accepted Requests */}
      {accepted.length > 0 && (
        <div className="bg-gray-100 p-4 rounded-lg mb-4">
          <h3 className="text-md font-semibold mb-2">Accepted Requests</h3>
          {accepted.map((user) => (
            <div key={user.id} className="flex items-center justify-between bg-white p-3 rounded-lg shadow mb-2">
              <div className="flex items-center gap-3">
                <img src={user.avatar} alt={user.name} className="w-10 h-10 rounded-full" />
                <div>
                  <p className="font-semibold">{user.name}</p>
                  <p className="text-sm text-gray-500">{user.role}</p>
                </div>
              </div>
              <FaCommentDots className="text-black text-lg cursor-pointer" />
            </div>
          ))}
        </div>
      )}

      {/* New Hackathons */}
      {hackathons.length > 0 && (
        <div className="bg-gray-100 p-4 rounded-lg">
          <h3 className="text-md font-semibold mb-2">New Hackathons</h3>
          {hackathons.map((hackathon) => (
            <div key={hackathon.id} className="flex items-center justify-between bg-white p-3 rounded-lg shadow mb-2">
              <div>
                <p className="font-semibold">{hackathon.title}</p>
                <p className="text-sm text-gray-500">{hackathon.startIn}</p>
              </div>
              <button className="bg-green-500 text-white px-3 py-1 rounded-lg">Join Now</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Notifications;